query:
-- Publish all ESL courses
UPDATE courses SET status = 'published' WHERE slug LIKE 'esl-%';

-- Publish all ESL modules (course_id range 200001-200006)
UPDATE course_modules SET status = 'published' WHERE course_id BETWEEN 200001 AND 200006;

-- Publish all ESL lessons
UPDATE lessons SET status = 'published' WHERE module_id IN (SELECT id FROM course_modules WHERE course_id BETWEEN 200001 AND 200006);

-- Publish all ESL activities
UPDATE activities SET status = 'published' WHERE lesson_id IN (SELECT id FROM lessons WHERE module_id IN (SELECT id FROM course_modules WHERE course_id BETWEEN 200001 AND 200006));

-- Publish all ESL learning paths
UPDATE learning_paths SET status = 'published' WHERE slug LIKE 'esl-%';

